mapper.update:
UPDATE MENU MEU SET
                MEU.PARENTID = #{parentid}
          WHERE
                MEU.MENU_ID = #{id}

mapper.insert:
begin delete from rolemenurelation where roleid=#{roleid};
insert into rolemenurelation( ROLT_ID, roleid, menuid, creatorid, createtime, modifierid, modifytime ) values( sys_guid(), #{roleid}, #{item.id}, #{item.creatorid}, (SELECT sysdate FROM DUAL), #{item.creatorid}, (SELECT sysdate FROM DUAL) )
end;
